var fireslider=$(".slider").fireSlider({active:2,show:3});$(document).ready(function(){var e=fireslider.data("fireSlider"),i=$("form#builder");i.find("a#update-slider").click(function(r){r.preventDefault();var a=parseInt(i.find('[name="show"]').val()),n=parseInt(i.find('[name="active"]').val());e.options.show=a>0?a:e.options.show,e.options.active=n>0&&a>=n?n:e.options.active,e.refresh()})});